package coreJava;

public class CoreJavaLearning_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// To store a number into a variable int 
		// Java supports integer, String, Double, Float, Double, Short, Long, boolean
		//Integer will allow only 9 bytes of value
		
		int num = 523444444;
		long num1 = 13223122323L;
		String name = "Dharani";
		float num11 = 5.99f;
		double num111 = 12332.12322331;
		boolean data = true;
		short num1111 = 8723;
		char name1 = 'a';
		
		System.out.println(num);
		System.out.println(num1);
		System.out.println(name);
		System.out.println(num11);
		System.out.println(num111);
		System.out.println(data);
		System.out.println(num1111);
		System.out.println(name1);
		
		//concatenation of two values
		
		System.out.println(num + " is the integer");
		
		

	}

}
